Get-ADUser -filter {Enabled -eq $true -and PasswordNeverExpires -eq $false -and PasswordExpired -eq $false} -Properties "Name", "EmailAddress", "msDS-UserPasswordExpiryTimeComputed" | 
Where-Object {$_."msDS-UserPasswordExpiryTimeComputed" -ne 0} | Select-Object -Property "Name", "EmailAddress", @{Name = "PasswordExpiry"; Expression = {[datetime]::FromFileTime($_."msDS-UserPasswordExpiryTimeComputed")}} | Where-Object {$_.PasswordExpiry -ge (Get-Date).Date -and $_.PasswordExpiry -le (Get-Date).Date.AddDays(7)}|Export-Csv "D:\$((Get-Date).ToString("dd-MM-yyyy"))_userlist.csv" -NoTypeInformation